<template>
  <div class="lamp-wrapper">
    <div
      :class="`lamp${index + 1}-icon`"
      v-for="(data, index) in dataItem"
      :key="index"
        v-bind:title="data.desc"
      @click="handleClick(data)"
    >
      <img
        src="data:image/png;base64,"
        alt=""
      />
      <span>{{ data.count }}</span>
    </div>
  </div>
</template>

<script>
import { alarmStatistics } from "@/api/home";
export default {
  components: {},
  props: {},
  data() {
    return {
      dataItem: [],
    };
  },
  watch: {},
  computed: {},
  methods: {
    handleClick(data) {
      this.$router.push({
        path: data.path,
        query: { alarmLevel: data.alarmLevel },
      });
    },
    getPolice() {
      let self = this;
      alarmStatistics({alarmStatus:0}).then((res) => {
        self.dataItem = res.data.map((data, index) => {
          data.path = "/warning/record";
          switch (index) {
            case 0:
              data.desc = "一级预警";
              break;
            case 1:
              data.desc = "二级预警";
              break;
            case 2:
              data.desc = "三级预警";
              break;
            case 3:
              data.desc = "四级预警";
              break;

            default:
              break;
          }
          return data;
        });
      });
    },
  },
  created() {
    let self = this;
    self.getPolice();
    setInterval(() => {
      self.getPolice();
    }, 1000 * 60);
  },
  mounted() {},
};
</script>
